package program;

public class Incrementdecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=5,b;
b=a++;
System.out.println("value of a:"+a+"value of b:"+b);
b=a--;
System.out.println("value of a:"+a+"value of b:"+b);
b=++a;
System.out.println("value of a:"+a+"value of b:"+b);
b=--a;
System.out.println("value of a:"+a+"value of b:"+b);
	}

}
