package program;

public class Pattern7 {

	public static void main(String[] args) {
// TODO Auto-generated method stub
		System.out.println("no of rows and colums");
		
int count=0;

for( int i=1;i<=4;i++)
{
	for( int j=1;j<=i;j++)
	{
		count++;
		System.out.print( count+"");
	}
	System.out.println(" ");
}
	}

}
