package program;

import java.util.Scanner;

public class Programofsum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner ab=new Scanner(System.in);
		int a=ab.nextInt();
		int sum=0;
		for(int i=1;i<a;i++)
		{
			sum=sum+i;
		}
		System.out.println("sum od digits:"+sum);
			
		

	}

	
		
	}


